package com.apps.abercrombiefitch.activity;import android.app.Activity;import android.content.Intent;import android.os.Build;import android.os.Bundle;import android.view.View;import android.view.Window;import android.webkit.WebSettings;import android.webkit.WebView;import android.webkit.WebViewClient;import com.apps.abercrombiefitch.R;public class AFWebViewActivity extends Activity {    @Override    public void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.af_web_view);        WebView webView = (WebView) findViewById(R.id.webView);        webView.getSettings().setJavaScriptEnabled(true);        webView.getSettings().setRenderPriority(WebSettings.RenderPriority.HIGH);        webView.getSettings().setCacheMode(WebSettings.LOAD_NO_CACHE);        webView.getSettings().setUseWideViewPort(true);        webView.getSettings().setLoadWithOverviewMode(true);        webView.setWebViewClient(new WebViewClient(){            @Override            public boolean shouldOverrideUrlLoading(WebView view, String url) {                return false;            }        });        Intent btnTitleOne = getIntent();        System.out.println("URL" + btnTitleOne.getExtras().getString("URI_FOR_ONE"));        webView.loadUrl(btnTitleOne.getExtras().getString("URI_FOR_ONE"));        Intent btnTitleTwo = getIntent();        webView.loadUrl(btnTitleTwo.getExtras().getString("URI_FOR_TWO"));        Intent btnTitleThree = getIntent();        webView.loadUrl(btnTitleThree.getExtras().getString("URI_FOR_THREE"));    }}